export default {
  name: "Zoom",
  data: function data() {
    return {
      currentIndex: 0
    };
  },
  props: ["skuImageList"],
  computed: {
    // 重点：避免服务前传来的数据是空，而报错
    imgObj: function imgObj() {
      return this.skuImageList[this.currentIndex] || {};
    }
  },
  mounted: function mounted() {
    var _this = this;

    // 兄弟组件通信,接受ImageList传递的数据
    this.$bus.$on("getIndex", function (index) {
      // console.log(index);
      //修改currentIndex的值,从而动态改变数据
      _this.currentIndex = index;
    });
  },
  methods: {
    //  放大镜的事件(event就是鼠标指针范围,left就是绿色遮罩层鼓励左边框的距离,top就是绿色遮罩层鼓励上边框的距离,mask就是绿色遮罩层,big就是放大后的图片)
    handler: function handler(event) {
      var mask = this.$refs.mask;
      var big = this.$refs.big;
      var left = event.offsetX - mask.offsetWidth / 2;
      var top = event.offsetY - mask.offsetHeight / 2; // 约束范围

      if (left <= 0) left = 0;
      if (left >= mask.offsetWidth) left = mask.offsetWidth;
      if (top <= 0) top = 0;
      if (top >= mask.offsetHeight) top = mask.offsetHeight; // 修改元素的left|top属性值

      mask.style.left = left + 'px';
      mask.style.top = top + 'px';
      big.style.left = -2 * left + 'px';
      big.style.top = -2 * top + 'px';
    }
  }
};
