export default {
  name: "Pagination",
  props: ["pageNo", "pageSize", "total", "continues"],
  computed: {
    // 总的页数
    totalPage: function totalPage() {
      // Math.ceil就是向上取整
      return Math.ceil(this.total / this.pageSize);
    },
    // 计算出连续页码中的起始数字和结束数字
    Continue: function Continue() {
      var continues = this.continues,
          pageNo = this.pageNo,
          totalPage = this.totalPage;
      var start = 0;
      var end = 0;

      if (totalPage < continues) {
        start = 1;
        end = totalPage;
      } else {
        start = pageNo - parseInt(continues / 2);
        end = pageNo + parseInt(continues / 2);

        if (start < 1) {
          start = 1;
          end = continues;
        }

        if (end > totalPage) {
          end = totalPage, start = totalPage - continues + 1;
        }
      }

      return {
        start: start,
        end: end
      };
    }
  }
};
