import Swiper from "swiper";
export default {
  name: "ImageList",
  data(){
    return{
     currentIndex:0
    }
  },
  props: ["skuImageList"],
  // 立即监听
  watch: {
    skuImageList: {
      //   立即监听：不管数据有没有变化，我上来立即监听一次
      immediate: true,
      handler(newValue, oldValue) {
        this.$nextTick(() => {
          var mySwiper = new Swiper(this.$refs.imgList, {
            //   direction: "vertical", // 垂直切换选项
            // loop: true, // 循环模式选项

            // 如果需要分页器
            // pagination: {
            //   el: ".swiper-pagination",
            //   clickable: true,
            // },

            // 如果需要前进后退按钮
            navigation: {
              nextEl: ".swiper-button-next",
              prevEl: ".swiper-button-prev",
            },
            // 显示几个图片设置
            slidesPerView:3,
            // 图片几个为一组
            slidesPerGroup:1

            // 如果需要滚动条
            // scrollbar: {
            //   el: ".swiper-scrollbar",
            // },
          });
        });
      },
    },
  },
  methods:{
    changeCurrentIndex(index){
      // 修改响应式数据  
      this.currentIndex=index;
      //兄弟组件通信,向组件Zoom传递数据
      this.$bus.$emit('getIndex',this.currentIndex)
    }
  }
};
