export default {
  name: "Register",
  data() {
    return {
      // 手机表单数据--手机号
      phone: "",
      // 验证码
      code: "",
      // 密码
      password: "",
      // 确认密码
      password1: "",
      // 是否同意协议
      agree: true,
    };
  },
  methods: {
    // 获取验证码
    async getCode() {
      try {
        // 如果获取到验证码
        const { phone } = this;
        // 简单判断一下--至少用数据
        phone && (await this.$store.dispatch("getCode", this.phone));
        // 组件中的code属性值变为仓库中的验证码
        this.code = this.$store.state.user.code;
      } catch (error) {
        alert(err.message);
      }
    },
    // 点击注册
    async userRegister() {
      // 如果正确，路由跳转
      try {
        const { phone, code, password, password1 } = this;
        (phone &&
          code&&password == password1 )&&
          (await this.$store.dispatch("UserRegister", {
            phone,
            code,
            password,
          }));
        this.$router.push("/login");
      } catch (error) {
        alert(error.message);
      }
    },
  },
};
